import CardItem from './CardItem'
import './Cards.css'

function Cards() {

  const [concertData, setConcertData] = useState(null);

    useEffect(() => {
        fetch('/next-concert') // Assuming this endpoint is available in your Express backend
            .then(response => response.json())
            .then(data => {
                setConcertData(data);
            })
            .catch(error => {
                console.error('Error fetching concert data:', error);
            });
    }, []);

    return (
      <div>
          {concertData ? <ConcertCard concert={concertData} /> : <p>Loading...</p>}
      </div>
  );

  function ConcertCard({ concert }) {
    const { name, dates, _embedded } = concert;
    const venue = _embedded.venues[0].name;
    const date = dates.start.localDate;
    const time = dates.start.localTime;

    return (
        <div className="concert-card">
            <h2>{name}</h2>
            <p>Venue: {venue}</p>
            <p>Date: {date}</p>
            <p>Time: {time}</p>
        </div>
    );
  }











